import React, { useState,useEffect } from "react";
import { Container, Grid , Button, Typography  ,FormControl,Select,MenuItem} from "@material-ui/core";
import { DataGrid } from '@material-ui/data-grid';
import ModalBranch from "./components/modalBranch";
import { getLines,getBranchByLine } from "../../services/api/ConfigService";

const BranchConfig =()=>{
const [ lines, setLines] = useState([]);
const [ lineid, setLineId ] = useState(0);

useEffect(() => {
    getLines().then(response =>{
        setLines(response.data);
    });
}, [true])

    const columns = [
        { field: 'id', headerName: 'ID', width: 90 },
        { field: 'firstName', headerName: 'DescripciÃ³n', width: 250},
        { field: 'edit', headerName: 'Editar', width: 160,
            renderCell: (params) => (
                <strong>
                    <Button
                        variant="contained"
                        size="small"
                        color="secondary"
                        style={{ marginLeft: 16 }}
                        onClick={''}

                    >
                        Editar
                  </Button>
                </strong>
            ),
        },
        { field: 'detail', headerName: 'Detalle', width: 160,
        renderCell: (params) => (
            <strong>
                <Button
                    variant="contained"
                    size="small"
                    color="secondary"
                    style={{ marginLeft: 16 }}
                    onClick={''}

                >
                    Detalle
              </Button>
            </strong>
        ),
    },
      ];

      const [ rows, setRows ] = useState([]);

      const [ openLine, setOpenLine ] = useState(false);
      const handleOpenLine =()=>{
        setOpenLine(!openLine);
      }

      const onclickbutton =()=>{
        handleOpenLine();
      }

    return(
        <Container maxWidth>
            <Grid container spacing={2}>
                <Grid item xs={12}>
                    <Typography variant={'h5'}> Marcas</Typography>
                </Grid>
                <Grid item xs={8}>
                <FormControl fullWidth variant="outlined"  > 
                    <Select 
                        fullWidth
                        label="Linea"
                        name="LineId"
                        onChange={(e =>{
                            const lineid = e.target.value;
                            getBranchByLine(lineid).then(response =>{
                                setRows(response.data);
                            })
                        })}
                        >
                        <MenuItem value="0">
                            <em>Seleccione</em>
                        </MenuItem>
                        {(lines.map((n) => 
                          <MenuItem value={n.id}>{n.name}</MenuItem>
                        ))}
                      </Select>

                    </FormControl>

                </Grid>
                <Grid item xs={2}>
                    <Button  fullWidth variant="contained">Buscar</Button>
                </Grid>
                <Grid item xs={2}>
                    <Button onClick={onclickbutton} fullWidth variant="contained">Nuevo</Button>
                    <ModalBranch isOpen={openLine} handleClose={handleOpenLine} />
                </Grid>
                <Grid item xs={12}>
                <div style={{ height: 400, width: '100%' }}>
                    <DataGrid
                        rows={rows}
                        columns={columns}
                        pageSize={5}
                        disableSelectionOnClick
                    />
                    </div>
                  </Grid>
            </Grid>
        </Container>
    );
}

export default BranchConfig;
