import HttpClient from "../HttpClient";
import axios from "axios";

export const saveClientServer = (model) =>{ 
    return new Promise((resolve, reject) => {
        HttpClient.post('/Client/SaveClient' , model).then(response => {
            resolve(response);
        }).catch(error => {
            resolve(error.response);
        })
    })
}

export const getClientById =(id)=> {
    return new Promise((resolve, reject) => {
         HttpClient.get('/Client/GetClientById?id='+ id).then(response => {
             resolve(response);
         }).catch(error => {
             resolve(error.response);
         })
     })
 }

 export const getClients =()=> {
    return new Promise((resolve, reject) => {
         HttpClient.get('/Client/GetClients').then(response => {
             resolve(response);
         }).catch(error => {
             resolve(error.response);
         })
     })
 }

 export const getRUC = (ruc)=>{
    const requestOptions = {
        token : "303144d4-680b-4930-88d5-6e3bc7060099-3f424b51-8da6-4935-a213-81b4a655c23c",
        ruc : "10746419916"
    };
    return new Promise((resolve, reject) => {
        axios.post('https://ruc.com.pe/api/v1/consultas', { requestOptions })
        .then(response => {
            resolve(response);
        })
    });
 }
