import HttpClient from "../HttpClient";


const token_seguridad = window.localStorage.getItem("token_id");
console.log(token_seguridad);

export const loginUser = (model,dispatch) =>{
    console.log(model);
    return new Promise((resolve, reject) => {
        HttpClient.post('/Employee/Login' , model).then(response => {
            resolve(response);
            dispatch({
                type:"SESSION_START",
                session : response.data,
                authenticated : true
            })
            window.localStorage.setItem("token_id",response.data.id);
            window.localStorage.setItem("token_employee",response.data);
        }).catch(error => {
            resolve(error.response);
        })
    })
}


export const getUserApps =()=> {
   return new Promise((resolve, reject) => {
        HttpClient.get('/RtUserAppApi/GetUserApps').then(response => {
            resolve(response);
        }).catch(error => {
            resolve(error.response);
        })
    })
}

export const getEmployeeActually =(dispatch)=> {
    return new Promise((resolve, reject) => {
         HttpClient.get('/Employee/GetEmployeeById?id='+ token_seguridad).then(response => {
             resolve(response);
             dispatch({
                type:"SESSION_START",
                session : response.data,
                authenticated : true
            })          
         }).catch(error => {
             resolve(error.response);
         })
     })
 }

export const getEmployeeById =(id)=> {
    return new Promise((resolve, reject) => {
         HttpClient.get('/Employee/GetEmployeeById?id='+ id).then(response => {
             resolve(response);
         }).catch(error => {
             resolve(error.response);
         })
     })
 }
