import React,{ useState, useEffect} from "react"; 
import { Container, Grid ,CardMedia, Typography,TextField, Button,Select,FormControl,MenuItem} from "@material-ui/core";
import logoicon from '../logoicon.jpeg';
import TableDemand from './components/tableDemand';
import { saveClientServer , getClientById , getRUC} from "../../services/api/ClientService";
import { saveRequestDocument , GetRequestsDocumentById,GetCorrelative} from "../../services/api/Demandservice";
import { getCompanyById} from "../../services/api/CompanyService";
import { withRouter } from 'react-router-dom';
import swal from 'sweetalert';


const Demand = (props) =>{
    const { match} = props;
    const demandid = match.params.demandid;
    const [ demandModel , setDemandModel ] = useState({
        RequestId : 0,
        Applicationseries : '0001',
        ClientID : 5,
        Contact : '',
        CorrelativeNumber : '',
        CustomerAddress : '',
        Dateofissue : '',
        IdCategory : 0,
        IdPriority : 0,
        Phone : '',
        Reason: '',
        Responsible: '',
        ScheduledDate: '',
        Adviser :'',
        RequestDeatils : []
    });

    const [ clientModel , setClientModel ] = useState({
        Id : 0,
        CustomerName : '',
        CustomerAddress: '',
        RUCClient : '',
        Active : true
    });

    const [ demandDetailModel , setDemandDetailModel] = useState({
        LineRequestDetail:'0',
        Mark:'0',
        MediumRequestDetail : '0',
        Model:'0',
        NumberRequestDetail:1,
        Place:'0',
        ServiceRequestDetail : '0',
        RequestId:0
    });

    const [ companyModel, setCompanyModel] = useState({
        address : '',
        branch : '',
        idCompany : 0,
        phone : '',
        ruc:'',
        socialReason : '',
        tradeName : ''
    });

    useEffect(() => {
        if (demandid != null) {
            GetRequestsDocumentById(demandid).then(response =>{
                console.log(response.data);
                const { data } = response;
                setDemandModel((last)=>({
                    ...last,
                    IdCategory : data.idCategory, 
                    Adviser : data.adviser,
                    Applicationseries : data.applicationseries,
                    Dateofissue : data.dateofissue,
                    IdPriority : data.idPriority,
                    ScheduledDate : data.scheduledDate,
                    Reason : data.reason,
                    Responsible : data.responsible,
                    Contact: data.contact,
                    Phone : data.phone,
                    CorrelativeNumber: data.correlativeNumber
                }));


                setDemandDetailModel((last)=>({
                    ...last,
                    LineRequestDetail : data.requestDeatils.lineRequestDetail,
                    Mark :  data.requestDeatils.mark,
                    MediumRequestDetail : data.requestDeatils.mediumRequestDetail,
                    Model:  data.requestDeatils.model,
                    NumberRequestDetail:1,
                    Place:  data.requestDeatils.place,
                    ServiceRequestDetail :  data.requestDeatils.serviceRequestDetail,
                    RequestId:data.requestId
                })); 

                getClientById(data.clientID).then(response =>{
                const { data } = response;
                console.log(response.data);
                    setClientModel((last)=>({
                        ...last,
                        Id : data.Id,
                        CustomerName: data.customerName,
                        CustomerAddress : data.customerAddress,
                        RUCClient : data.rucClient
                    }));
                });
            })
        }else{
            GetCorrelative().then(response =>{
                setDemandModel({

                    CorrelativeNumber : response.data
                })
            });
        }
        getCompanyById(1).then(response =>{
            console.log(response.data);
            setCompanyModel(response.data);
        })


    }, [demandid])

    const saveDemand = (e) =>{
        const { name, value} = e.target;
        setDemandModel(last => ({
            ...last,
            [name]: value
        }))
    }

    const saveClient = (e) =>{
        const { name, value} = e.target;
        setClientModel(last => ({
            ...last,
            [name]: value
        }))
    }

    const sendClient = () => {

        saveClientServer(clientModel).then(response => {
            const { data } = response;
            var clientId = data;

            setClientModel(last => ({
                ...last,
                Id: clientId
            }))

           saveRequestDocument(demandModel, demandDetailModel,clientId).then(response =>{
              console.log(response);

              swal("Buen trabajo!", "La solicitud a sido guardada", "success")
              .then(() => {
                props.history.push('/listdemand');
              });
           });

        }); 
    }

    const getLocalRuc = ()=>{
        getRUC(clientModel.RUCClient).then(response =>{
            console.log(response.data);

            setClientModel((last)=>({
                ...last,
                Id : response.data.id,
                CustomerName: response.data.customerName,
                CustomerAddress : response.data.customerAddress,
                RUCClient : response.data.rucClient
            }));

            setDemandModel((last) =>({
                ...last,
                IdCategory : response.data.categoryId
            }))

        });
    }


    return(
        <Container fullwidth style={{marginTop:50}} >
            <Grid container spacing={2}>
                <Grid item  md={3} xs={4} >
                <CardMedia
                    style={{height: 200, width: 200}}
                    component="img"
                    alt="Biossay"
                    height="auto"
                    image={logoicon}
                />
                </Grid>
                <Grid item  md={5} xs={8}>
                    <Typography variant="h4" style={{ fontWeight:"bold" ,height:"30%"}}>{companyModel.tradeName}</Typography>
                    <Typography style={{ height:"30%"}}>{companyModel.address}</Typography>
                    <Typography style={{ height:"10%"}}>TELEFONO: {companyModel.phone}</Typography>
                    <Typography style={{ height:"10%"}}>CEL: 991463701 / 980658053</Typography>
                    <Typography style={{ height:"10%"}}>EMAIL : gerencia@biossay.com.pe</Typography>
                </Grid>
                <Grid item  md={4} xs={12} style= {{ borderStyle : "solid",textAlign:"center"}} >
                    <Typography  variant="h5"  style={{ height:"33%"}}>RUC {companyModel.ruc}</Typography>
                    <Typography  variant="h5"  style={{ height:"33%" ,fontWeight:"bold" }}>SOLICITUD DE SERVICIO</Typography>
                    <Typography  variant="h5"  style={{ height:"33%"}}>0001-{demandModel.CorrelativeNumber}</Typography>
                </Grid>

                <Grid container md={6} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> RUC </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                        <TextField type="number" fullWidth id="outlined-basic" label="" variant="outlined" onBlur={getLocalRuc}   onChange={saveClient} value={clientModel.RUCClient} name="RUCClient" />
                    </Grid>
                </Grid>
                <Grid container md={6} direction="row"style={{marginTop:20}}  alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> F. EMISIÓN </Typography> 
                    </Grid>
                    <Grid item md={9} xs={8} direction="row">  
                        <TextField type="datetime-local" fullWidth id="outlined-basic" onChange={saveDemand} variant="outlined" value={demandModel.Dateofissue} name="Dateofissue" />
                    </Grid>
                </Grid>
                <Grid container md={6} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> CLIENTE </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                        <TextField fullWidth id="outlined-basic"  onChange={saveClient} variant="outlined" value={clientModel.CustomerName} name="CustomerName"  />
                    </Grid>
                </Grid>
                <Grid container md={6} direction="row" style={{marginTop:20}}  alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> PRIORIDAD</Typography> 
                    </Grid>
                    <Grid item md={9} xs={8} direction="row">  
                    <FormControl fullWidth variant="outlined"  > 
                        <Select
                        labelId="demo-simple-select-outlined-label"
                        id="demo-simple-select-outlined"
                        value={demandModel.IdPriority} 
                        fullWidth
                        onChange={saveDemand}
                        label="Age"
                        name="IdPriority"
                        >
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        <MenuItem value={1}><input type="color" value="#FF0000" disabled /></MenuItem>
                        <MenuItem value={2}><input type="color" value="#1DEE2D" disabled /></MenuItem>
                        <MenuItem value={3}><input type="color" value="#EEEA0D" disabled /></MenuItem>
                        <MenuItem value={4}><input type="color" value="#DDDDD8" disabled /></MenuItem>
                        <MenuItem value={5}><input type="color" value="#3F80CA" disabled /></MenuItem>
                        </Select>

                    </FormControl>
                    </Grid>
                </Grid>
                <Grid container md={6} direction="row"  style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> DIRECCIÓN </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                        <TextField fullWidth id="outlined-basic"  onChange={saveClient} label="" variant="outlined" name="CustomerAddress" value={clientModel.CustomerAddress} />
                    </Grid>
                </Grid>
                <Grid container md={6} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> RESPONSABLE </Typography> 
                    </Grid>
                    <Grid item md={9} xs={8} direction="row">  
                        <TextField fullWidth id="outlined-basic" name="Responsible" onChange={saveDemand} value={demandModel.Responsible} label="" variant="outlined" />
                    </Grid>
                </Grid>
                <Grid container md={6} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> CATEGORÍA </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                    <FormControl fullWidth variant="outlined"  > 
                        <Select
                        labelId="demo-simple-select-outlined-label"
                        id="demo-simple-select-outlined" 
                        fullWidth
                        label="Age"
                        name="IdCategory"
                        value={demandModel.IdCategory}
                        onChange={saveDemand}
                        >
                        <MenuItem value="">
                            <em>Seleccione</em>
                        </MenuItem>
                        <MenuItem value={1}>SIN CATEGORIA</MenuItem>
                        <MenuItem value={2}>BRONZE</MenuItem>
                        <MenuItem value={3}>SILVER</MenuItem>
                        <MenuItem value={4}>GOLD</MenuItem>
                        <MenuItem value={5}>PLATINUM</MenuItem>
                        <MenuItem value={6}>DIAMOND</MenuItem>
                        </Select>

                    </FormControl>
                    </Grid>
                </Grid>
                <Grid container md={6} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> F. PROGAMADA </Typography> 
                    </Grid>
                    <Grid item md={9} xs={8}  direction="row">  
                        <TextField type="datetime-local" fullWidth id="outlined-basic" onChange={saveDemand} label="" variant="outlined" name="ScheduledDate" value={demandModel.ScheduledDate} />
                    </Grid>
                </Grid> 

                <div style={{ height: 150, width: '100%',marginTop:20,marginBottom:20,overflow: "auto" }}>
                   <TableDemand demandDetailModel={demandDetailModel} setDemandDetailModel={setDemandDetailModel}/>

                </div>
                <Grid container md={12} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> ASESOR COMERCIAL </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                        <TextField fullWidth id="outlined-basic" label="" onChange={saveDemand} variant="outlined" name="Adviser" value={demandModel.Adviser} />
                    </Grid>
                </Grid>
                <Grid container md={12} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> MOTIVO </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                        <TextField fullWidth id="outlined-basic" label="" onChange={saveDemand} variant="outlined" name="Reason" value={demandModel.Reason} />
                    </Grid>
                </Grid>
                <Grid container md={12} direction="row" style={{marginTop:20}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> CONTACTO </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                        <TextField fullWidth id="outlined-basic" label="" onChange={saveDemand} variant="outlined" name="Contact" value={demandModel.Contact} />
                    </Grid>
                </Grid>
                <Grid container md={12} direction="row" style={{marginTop:20,marginBottom:30}} alignItems="center">
                    <Grid item md={3} xs={4}> 
                        <Typography> TELÉFONO </Typography> 
                    </Grid>
                    <Grid item md={8} xs={8} direction="row">  
                        <TextField fullWidth id="outlined-basic" label="" onChange={saveDemand} variant="outlined" name="Phone" value={demandModel.Phone} />
                    </Grid>

                </Grid>

                <Grid container md={12}  style={{marginTop:20,marginBottom:30,textAlign:"right"}} >
                    <Button onClick={sendClient} color="primary" style={{ color : "white"}} variant="contained">Guardar</Button>
                </Grid>
            </Grid>
        </Container>

    );
}

export default withRouter(Demand);
